// カラーからスキンのURLを取得する連想配列
const ColorToUrl = {
    red: 'data:image/png;base64,',
    blue: 'data:image/png;base64,',
    green: 'data:image/png;base64,',
    pink: 'data:image/png;base64,',
    orange: 'data:image/png;base64,',
    yellow: 'data:image/png;base64,',
    black: 'data:image/png;base64,',
    white: 'data:image/png;base64,',
    purple: 'data:image/png;base64,',
    brown: 'data:image/png;base64,',
    cyan: 'data:image/png;base64,',
    lime: 'data:image/png;base64,',
    maroon: 'data:image/png;base64,',
    rose: 'data:image/png;base64,',
    banana: 'data:image/png;base64,',
    gray: 'data:image/png;base64,',
    tan: 'data:image/png;base64,',
    coral: 'data:image/png;base64,'
};

// カラーリスト
const ColorList = Object.keys(ColorToUrl);

// カラーパネルを設置する
const setColorPanel = (reactid, top, left) => {
    $('.ext-color-panel').remove();
    const panel = $('<div>', { class: 'ext-color-panel' });
    ColorList.forEach((color) => {
        const item = $('<div>', { color });
        $(panel).append(item);
    });
    $(panel).data('reactid', reactid);
    $(panel).css({ top, left });
    $(panel).appendTo('body');
};

// クリックイベント: スキン
$(document).on('click', '.voice-state', (e) => {
    const reactid = (() => {
        const text = $(e.currentTarget).data('reactid') || '';
        const result = text.match(/\d+$/);
        return result === null ? '' : result[0];
    })();
    if (!reactid) return;
    const top = window.pageYOffset + e.clientY;
    const left = window.pageXOffset + e.clientX;
    setColorPanel(reactid, top, left);
});

// マウスダウンイベント: ドキュメント
$(document).on('mousedown', (e) => {
    // カラーパネル以外の場合 -> カラーパネルを削除する
    if ($(e.target).closest('.ext-color-panel').length === 0) {
        $('.ext-color-panel').remove();
    }
});

// クリックイベント: カラー -> 対象スキンのカラーを設定する
$(document).on('click', '.ext-color-panel > [color]', (e) => {
    // カラーを取得する
    const color = $(e.currentTarget).attr('color');
    if (!ColorList.includes(color)) return;
    // カラーパネルを取得する
    const panel = $(e.currentTarget).closest('.ext-color-panel');
    if ($(panel).length === 0) return;
    // IDを取得する
    const reactid = $(panel).data('reactid');
    if (!reactid) return;
    // スキンのカラーを更新する
    $(`style[reactid="${reactid}"]`).remove();
    const style = $('<style>', { reactid, text: `.voice-state[data-reactid*="${reactid}"] .avatar { background-image: url("${ColorToUrl[color]}") }` });
    $('head').append(style);
    // メニューを削除する
    $(panel).remove();
});
